var employeePageContainer = document.querySelector(".content");
var createClientbutton = document.querySelector(".ClientButton");

createClientbutton.addEventListener("click", () => {
    getViewData("/createClient")
    tableActive = false;
});


var tableActive = false;

export function getTableActive(){
    return tableActive;
}
export function setTableActive(isActive){
     tableActive = isActive;
}






export function getViewData(url){
    fetch(url, {
        method: "GET",
        headers: {
            "Content-Type": "application/json",
        },
    })
        .then((response) => {
            if (response.ok) {
                return response.json();
            } else if (response.status === 401) {
                // Incorrect password
                return response.json();
            } else {
                // Handle other response status codes or errors
                throw new Error("An error occurred");
            }
        })
        .then((data) => {
            if (data.message == NaN) {
                console.log(data);
            } else {
                employeePageContainer.innerHTML = data.message;
            }
        })
        .catch((error) => {
            console.error(error);
        });
}

