var custumer = document.querySelector(".clients_wrapper");
var custumerBtn = document.querySelector(".active");
custumerBtn.addEventListener("click", (e) => {
    e.preventDefault();
    custumer.classList.toggle("active");
});



//Takes all clients and putthem in the div
var data = getClients();
function displayClients(data) {
    const clientsDiv = document.getElementById("clients");
    data.forEach((client) => {
        const vatNumber = client.vatnumber;
        // Create a div element
        const clientDiv = document.createElement("div");
        // Set the text content to vatNumber
        clientDiv.textContent = vatNumber;
        // Set the id of the div to "clientsVatnumber"
        clientDiv.id = "clientsVatnumber";
        // Append the div to the clientsDiv
        clientsDiv.appendChild(clientDiv);
    });
}
function getClients() {
    const url = "/getClients";
    fetch(url)
        .then((response) => {
            if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);
            }
            return response.json();
        })
        .then((data) => {
            displayClients(data.message);
        })
        .catch((error) => {
            console.error("Fetch error:", error);
        });
}



