import { getTableDataContainer } from ".././populateTable.min.js";

function exportToExcel() {
    var tableDataContainer = getTableDataContainer()
    const wb = XLSX.utils.book_new();
    const ws = XLSX.utils.json_to_sheet(tableDataContainer);
    XLSX.utils.book_append_sheet(wb, ws, 'Sheet1');
    const wbout = XLSX.write(wb, { bookType: 'xlsx', type: 'array' });
    saveAs(new Blob([wbout], { type: 'application/octet-stream' }), 'table_data.xlsx');
}

const excelButton = document.getElementById('excel');
excelButton.addEventListener('click', exportToExcel);

