let dataTable;

export function putTableInfo(id) {
    const faturaData = {
        vatnumber: id,
    };

    fetch("/tableinfo", {
        method: "POST",
        headers: {
            "Content-Type": "application/json",
        },
        body: JSON.stringify(faturaData),
    })
    .then((response) => {
        if (response.ok) {
            return response.json();
        } else if (response.status === 401) {
            // Incorrect password
            return response.json();
        } else {
            // Handle other response status codes or errors
            throw new Error("An error occurred");
        }
    })
    .then((data) => {
        if (!dataTable) {
            // If DataTable is not initialized, initialize it
            dataTable = $('#example').DataTable({
                columns: Object.keys(data[0] || {}).map(key => ({ data: key })),
            });
        }

        // Clear existing rows and add new data
        dataTable.clear().rows.add(data).draw();
    })
    .catch((error) => {
        console.error("An error occurred:", error);
    });
}


